resources:
    // 로그인 유저 정보 조회
    select u.user_id, u.user_name, u.name,
(select count(*) from following where user_id = u.user_id) as following_count,
(select count(*) from follower where user_id = u.user_id) as follower_count
from user u
where u.user_name = \'%1$s\' and u.password = \'%2$s\'
    // 팔로워, 팔로잉 목록 조회
    SELECT user_id, user_name, name FROM user WHERE user_id IN (SELECT %1$s FROM %2$s WHERE user_id = %3$d)
    // 비밀번호 변경
    UPDATE user SET password = \'%1$s\' WHERE password = \'%2$s\' AND user_id = %3$d;
    // 회원가입
    INSERT into user(user_name, password) VALUES (\'%1$s\', \'%2$s\')
    UPDATE user SET password = \'%1$s\' WHERE password = \'%2$s\' AND user_id = %3$d;
